import { useEffect, useState } from "react";


function Articles() {
  return (
    <main className="articles-page">
      <h1>Articles</h1>
      <div className="articles-container">
        {articles.map((article) => (
          <div key={article.id} className="article-box">
            <img src={article.image || "/assets/article-placeholder.png"} alt={article.title} />
            <h2>{article.title}</h2>
            <p>{article.summary}</p>
            <button className="read-more">See More</button>
          </div>
        ))}
      </div>
    </main>
  );
}

export default Articles;
