import { useEffect, useState } from "react";

function Home() {
  const [showFirstContent, setShowFirstContent] = useState(true);

  useEffect(() => {
    const handleScroll = () => {
      // Swap content when scroll passes 200px
      setShowFirstContent(window.scrollY < 200);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <section id="home">
      <div className="hero-image">
        <img src="/Fekadu.jpg" alt="Lawyer" />
      </div>

      <div className={`hero-content ${showFirstContent ? "fade-in" : "fade-out"}`}>
        {showFirstContent ? (
          <>
            <h1>Fekadu Hailemariam</h1>
            <p>Senior Attorney at Law</p>
            <button className="contact-button">Schedule a Consultation</button>
          </>
        ) : (
          <>
            <p>
              Providing humble service with proud{" "}
              <span className="emphasis">15+</span> years of experience, from the early days
              of Law school to today's old age endeavors. Find out more by clicking on the
              button!
            </p>
            <button className="CV">Download CV here</button>
          </>
        )}
      </div>
    </section>
  );
}

export default Home;
