import React from "react";
import { useParams, Link } from "react-router-dom";

const ArticleDetail = () => {
  const { id } = useParams();

  // Placeholder article data
  const article = {
    id,
    title: `Article Title ${id}`,
    content: `This is the full content for article ${id}. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed at egestas sapien.`,
    date: "August 13, 2025",
    author: "Fekadu Hailemariam",
  };

  return (
    <div className="article-detail">
      <h1>{article.title}</h1>
      <p className="article-meta">
        {article.date} • by {article.author}
      </p>
      <div className="article-content">
        <p>{article.content}</p>
      </div>
      <Link to="/articles" className="back-button">
        ← Back to Articles
      </Link>
    </div>
  );
};

export default ArticleDetail;
