function Articles({ articles = [] }) {
  // Placeholder data if articles is empty
  const displayArticles = articles.length ? articles : [
    {
      id: 1,
      title: "Understanding Ethiopian Law",
      summary: "A short summary about Ethiopian law and legal practice.",
      image: "/assets/article-placeholder.png"
    },
    {
      id: 2,
      title: "Contract Drafting Tips",
      summary: "Tips and tricks for drafting watertight contracts.",
      image: "/assets/article-placeholder.png"
    },
    {
      id: 3,
      title: "Legal Ethics Explained",
      summary: "An overview of ethics for attorneys in practice.",
      image: "/assets/article-placeholder.png"
    },
  ];

  return (
    <main className="articles-page">
      <h1>Articles</h1>
      <div className="articles-container">
        {displayArticles.map((article) => (
          <div key={article.id} className="article-box">
            <img src={article.image} alt={article.title} />
            <h2>{article.title}</h2>
            <p>{article.summary}</p>
            <button className="read-more">See More</button>
          </div>
        ))}
      </div>
    </main>
  );
}


export default Articles;
